import styled, { keyframes } from "styled-components";
import { Link } from "react-router-dom";
import { createGlobalStyle } from "styled-components";

export const header = keyframes`
  0% {
    top: -100px;
  }
  100% {
  top: 80px;
  }
`;
export const Header = styled.h1`
  position: relative;
  text-align: center;
  color: white;
  font-family: Arial, Helvetica, sans-serif;
  animation: ${header} 2s;
`;

export const ErrorText = styled.p`
  font-size: 10px;
  color: red;
`;

export const CreateAccountButton = styled.input`
  display: flex;
  flex: auto;
  justify-content: center;
  height: 46px;
  background-color: #42c41a;
  border: none;
  color: white;
  margin: 20px 0;
  border-radius: 3px;
`;

export const CreateAccountFormContainer = styled.div`
  width: 700px;
  display: flex;
  flex-wrap: wrap;
  flex: auto;
`;

export const ContainerForCreateAccountFormContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 100px;
  flex-direction: column;
  align-items: center;
`;

export const InputErrorColor = styled.input`
  border-bottom: 1px solid red;
  background-color: rgba(255, 0, 0, 0.171);
`;

export const InputNormalColor = styled.input`
  height: 46px;
  width: 323px;
  border-radius: 5px;
  border: none;
  padding: 0 10px;
`;

export const LabelAndInputRow = styled.div`
  display: flex;
  flex-direction: column;
  padding: 10px 0;
`;

export const LabelAndInputRowContainer = styled.div`
  display: flex;
  flex: 1;
  flex-wrap: wrap;
  justify-content: space-between;
`;

export const LinkForLogin = styled(Link)`
  color: white;
  text-decoration: none;
  border: 1px solid white;
  padding: 10px;
  border-radius: 3px;
  position: relative;
  right: 30px;
  top: 40px;
  font-size: 20px;
`;
export const LinkForLoginContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;

export const GlobalStyle = createGlobalStyle`
  body {
    background-image: linear-gradient(to right top, #051937, #004d7a, #008793, #00bf72, #a8eb12);
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
  }
`;
