import React from "react";
import { Nav, Ul, Li, LinkForLogout, LinkForHome } from "./NavBar.jsx";
import SideTrigger from "./SideTrigger.js";
import SideNavBar from "./SideNavBar.js";

class NavBar extends React.Component {
  state = {
    sideBar: false
  };

  handleSideBar = () => {
    this.setState(prevState => {
      return { sideBar: !prevState.sideBar };
    });
  };
  render() {
    const { sideBar } = this.state;
    let sideNavBar;
    if (sideBar) {
      sideNavBar = <SideNavBar />;
    }
    return (
      <div>
        {!props.isAuthenticated ? (
          <Li>
            <LinkForHome to="/">Home</LinkForHome>
          </Li>
        ) : (
          <div>
            {sideNavBar}
            <SideTrigger onClick={this.handleSideBar} />
            <Nav>
              <Ul>
                <Li>
                  <LinkForLogout to="/secret">My Profile</LinkForLogout>
                </Li>
                <Li>
                  <LinkForLogout to="/logout">Logout</LinkForLogout>
                </Li>
              </Ul>
            </Nav>
          </div>
        )}
      </div>
    );
  }
}

export default NavBar;
