import React, { Component } from "react";
// import "./Home.jsx";
import "./Login.jsx";
import { Container, Text, Description, Span } from "./LoggInn.jsx";
import axios from "axios";
import classNames from "classnames";
import { Label } from "./LoggInn.jsx";

class LoggInn extends Component {
  _isMounted = true;
  state = {
    message: "Loading...",
    email: "",
    password: "",
    error: ""
  };

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors
      });
    }
  }

  handleInputChange = event => {
    const { value, name } = event.target;
    this.setState({
      [name]: value
    });
  };

  onSubmit = event => {
    event.preventDefault();
    axios
      .post("http://localhost:8080/api/authenticate", this.state)
      .then(res => {
        if (res.data.token) {
          sessionStorage.setItem("token", res.data.token);
          this.props.history.push("/secret");
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .catch(err => {
        this.setState({ error: err.response.data.error });
      });
  };

  componentDidMount() {
    fetch("http://localhost:8080/api/home")
      .then(res => res.text())
      .then(res => {
        if (this._isMounted) {
          this.setState({ message: res });
        }
      });
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  render() {
    let inputColor = classNames({
      redInput: this.state.error
    });
    return (
      <div className="background-image-for-login">
        <Container>
          <Text>
            <Description>{this.state.message}</Description>
          </Text>
        </Container>
        <div className="container-for-form-container">
          <FormContainer>
            <form onSubmit={this.onSubmit}>
              <p>Avagram</p>

              <div className="row">
                <Label>Email</Label>
                <input
                  className={[inputColor, "input-for-logginn"].join(" ")}
                  type="email"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleInputChange}
                  required
                />
              </div>
              <Span>{this.state.error}</Span>

              <div className="row">
                <label className="label">Password</label>
                <input
                  className={[inputColor, "input-for-logginn"].join(" ")}
                  type="password"
                  name="password"
                  value={this.state.password}
                  onChange={this.handleInputChange}
                />
              </div>
              <Span>{this.state.error}</Span>
              <div className="container-for-login-button">
                <label className="checkbox">
                  <input type="checkbox" className="input-for-checkbox" />
                  <span class="checkmark" />
                  Remember me
                </label>

                <input
                  className="log-in-button input round-input-button"
                  type="submit"
                  value="Log In"
                />
              </div>
            </form>
          </FormContainer>
        </div>
      </div>
    );
  }
}

export default LoggInn;
