import React, { Component } from "react";
import "./LoggInn.jsx";
import { Link } from "react-router-dom";
import "./Login.jsx";
import axios from "axios";
import classNames from "classnames";
import { Header } from "./Home.jsx";

class Home extends Component {
  state = {
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    error: ""
  };

  onSubmit = event => {
    event.preventDefault();
    axios
      .post("http://localhost:8080/api/register", this.state)
      .then(res => {
        if (res.data.token) {
          sessionStorage.setItem("token", res.data.token);
          this.props.history.push("/secret");
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .catch(err => {
        this.setState({ error: err.response.data });
      });
  };

  handleInputChange = event => {
    const { value, name } = event.target;
    this.setState({
      [name]: value
    });
  };
  render() {
    const { error } = this.state;
    let inputError = classNames({
      inputErrorColor: error
    });
    return (
      <div>
        <Link to="/authenticate" className="account">
          Login
        </Link>
        <div className="container-for-create-account-form-container">
          <Header>Create Account</Header>
          <div className="create-account-form-container">
            <form onSubmit={this.onSubmit}>
              <div className="create-account-input-row">
                <label className="create-account-label-for-input">
                  First Name
                </label>
                <input
                  className={inputError}
                  type="firstName"
                  name="firstName"
                  placeholder="First Name"
                  value={this.state.firstName}
                  onChange={this.handleInputChange}
                  require="true"
                />
              </div>
              {error && <p className="error-text error-input-row">{error}</p>}

              <div className="create-account-input-row">
                <label className="create-account-label-for-input">
                  Last Name
                </label>
                <input
                  className={inputError}
                  type="lastName"
                  name="lastName"
                  placeholder="Last Name"
                  value={this.state.lastName}
                  onChange={this.handleInputChange}
                  require="true"
                />
              </div>
              {error && <p className="error-text error-input-row">{error}</p>}
              <div className="create-account-input-row">
                <label className="create-account-label-for-input">Email</label>
                <input
                  className={inputError}
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={this.state.email}
                  onChange={this.handleInputChange}
                  require="true"
                />
              </div>
              {error && <p className="error-text error-input-row">{error}</p>}
              <div className="create-account-input-row">
                <label className="create-account-label-for-input">
                  Password
                </label>
                <input
                  className={inputError}
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={this.state.password}
                  onChange={this.handleInputChange}
                  require="true"
                />
              </div>
              {error && <p className="error-text error-input-row">{error}</p>}
              <input
                className="button create-account-button"
                type="submit"
                value="Create Account"
              />
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
